d3.csv('fifa_ranking.csv')
	.then(fifa_ranking => {
		let fifa_ranking_data = fifa_ranking.slice(0, 10).sort((a, b) => {
			return a.previous_points - b.previous_points
		})
		console.log(fifa_ranking_data)
		d3.select('#chartBar').selectAll('div')
			.data(fifa_ranking_data)
			.enter()
			.append('div')
			.transition().duration(500)
			.attr('class', 'bar')
			.text(function (fifa_ranking_data) {
				return 'ranking ' + fifa_ranking_data.rank + ' ' + fifa_ranking_data.country_full + ' ' + fifa_ranking_data.previous_points + ' points'
			})
			.style('width', function (fifa_ranking_data) {
				return fifa_ranking_data.previous_points * 5 + 'px'
			})

		//-----------------------------------------------------------------------------------------------


		const svg1 = d3.select('#chartCircle')
			.append('svg')
			.attr('width', 1500)
			.attr('height', 300)

		const circle = svg1.selectAll('circle')
			.data(fifa_ranking_data.sort((a, b) => {
				return a.rank - b.rank
			}))
			.enter()
			.append('circle')
			.attr('class', 'foo')
			.transition().duration(500)
			.attr('cx', (fifa_ranking_data, i) => {
				return i * 150
			})
			.attr('cy', (fifa_ranking_data, i) => {
				return 150
			})
			.attr('r', function (fifa_ranking_data) {
				let data = fifa_ranking_data.previous_points
				return data
			})
			.style('fill', function (fifa_ranking_data) {
				if (fifa_ranking_data.previous_points < 55) {
					return 'red'
				} else {
					return 'green'
				}
			})

		svg1.selectAll('text')
			.data(fifa_ranking_data)
			.enter()
			.append('text')
			.transition().duration(500)
			.attr('x', (fifa_ranking_data, i) => {
				return i * 150
			})
			.attr('y', (fifa_ranking_data, i) => {
				return 70
			})
			.attr('r', function (fifa_ranking_data) {
				let data = fifa_ranking_data.previous_points
				return data
			})
			.text(function (fifa_ranking_data) {
				return 'rank ' + fifa_ranking_data.rank + ' \n\n' + fifa_ranking_data.country_full
			})
			.style('fill', function (fifa_ranking_data) {
				if (fifa_ranking_data.previous_points < 55) {
					return 'red'
				} else {
					return 'green'
				}
			})
			.style('font-size', 12)


		//-----------------------------------------------------------------------------------------------
		const data = [{
			"letter": "q",
			"presses": 1
		}, {
			"letter": "w",
			"presses": 5
		}, {
			"letter": "e",
			"presses": 2
		}]
		const width = 1000,
			height = 1000,
			radius = Math.max(width, height) / 2
		const color = d3.scaleOrdinal()
			.range(["green", "red", "blue", "grey", "magenta", "pink", "brown", "silver", "yellow", "maroon"])
		const pie = d3.pie()
			.value(function (d) {
				return d.previous_points
			})(fifa_ranking_data)
		const arc = d3.arc()
			.outerRadius(radius - 70)
			.innerRadius(0)
		const labelArc = d3.arc()
			.outerRadius(radius - 100)
			.innerRadius(radius - 40)
		const svg = d3.select("#chartPie")
			.append("svg")
			.attr("width", width)
			.attr("height", height)
			.append("g")
			.attr("transform", "translate(" + width / 2 + "," + height / 2 + ")") // Moving the center point. 1/2 the width and 1/2 the height
		const g = svg.selectAll("arc")
			.data(pie)
			.enter().append("g")
			.attr("class", "arc")
		g.append("path")
			.transition().duration(500)
			.attr("d", arc)
			.style("fill", function (d) {
				return color(d.data.rank)
			})
		g.append("text")
			.transition().duration(500)
			.attr("transform", function (d) {
				return "translate(" + labelArc.centroid(d) + ")"
			})
			.text(function (d) {
				return d.data.previous_points + ' ' + d.data.country_full
			})
			.style("fill", "black")
	})
	.catch(err => {
		console.log(err)
	})
